import React, { useState } from 'react';
import { BrowserRouter, Route, Routes } from "react-router-dom";

import Header from './components/Header'; // Importa el componente Header
import Body from './components/Body'; 
import Idioms from "./components/Idioms";
import Preferences from "./components/Preferences";
import AddStudent from "./components/AddStudent";
import GetAllStudents from "./components/GetAllStudents";
import Footer from "./components/Footer";
import Main from "./components/Main";


import './styles.css'; // Importa el archivo de estilos CSS global

function App() {
  const [currentComponent, setCurrentComponent] = useState('main'); // Estado para controlar el componente actualmente mostrado

  // Función para cambiar el componente actual
  const changeComponent = (component) => {
    setCurrentComponent(component);
  };

  return (
    <div className="app">
      <BrowserRouter>
        <Header changeComponent={changeComponent} /> {/* Renderiza el componente Header y pasa la función changeComponent */}
        <Body currentComponent={currentComponent} /> {/* Renderiza el componente Body y pasa el componente actualmente mostrado */}
        <Routes>
          <Route path="/idioms" element={<Idioms/>} />
          <Route path="/preferences" element={<Preferences/>} />
          <Route path="/addStudent" element={<AddStudent/>} />
          <Route path="/getAllStudents" element={<GetAllStudents/>} />
        </Routes>
        <Footer />
      </BrowserRouter>
    </div>
  );
}

export default App;
