import React, { useState, useEffect } from 'react';

function AddStudentComponent() {
  // Definición de estados utilizando el hook useState
  const [data, setData] = useState([]); // Estado para almacenar datos obtenidos del servidor
  const [nombre, setNombre] = useState("");
  const [sexo, setSexo] = useState("");
  const [dni, setDni] = useState("");
  const [fechanacimiento, setFechanacimiento] = useState("");
  const [estadocurriculum, setEstadocurriculum] = useState("");
  const [estadoadmision, setEstadoadmision] = useState("");
  const [emailinstituto, setEmailinstituto] = useState("");
  const [nacionalidad, setNacionalidad] = useState("");
  const [carnetconducir, setCarnetconducir] = useState("");
  const [disponibilidad, setDisponibilidad] = useState("");
  const [numeroSS, setNumeroSS] = useState("");
  const [situacionlaboral, setSituacionlaboral] = useState("");
  const [nombretutorlegal, setNombretutorlegal] = useState("");
  const [dnitutorlegal, setDnitutorlegal] = useState("");
  const [especialidad, setEspecialidad] = useState("");
  const [telalumno, setTelalumno] = useState("");
  const [telfamilia, setTelfamilia] = useState("");
  const [email, setEmail] = useState("");
  const [observaciones, setObservaciones] = useState("");
  const [mesFCT, setMesFCT] = useState("");
  //Variables necesarias para poder usar /addStudent_Idiom
  const [data2, setData2] = useState([]);
  const [idiomaalumno, setIdiomaAlumno] = useState("");
  const [ididioma, setIdIdioma] = useState("");
  const [idalumno, setIdAlumno] = useState("");
  const [titulo, setTitulo] = useState("");
  //Variables necesarias para poder usar /addStudentPreferences
  const [opcion1, setOpcion1] = useState("");
  const [opcion2, setOpcion2] = useState("");
  const [opcion3, setOpcion3] = useState("");
  const [fecha, setFecha] = useState("");
  //Variables necesariasa para poder usar /addStudent_Doc
  const [docalum, setDocAlum] = useState("");
  const [url, setUrl] = useState("");
  //Variables necesarias para poder usar /addStudent_Adress
  const [domicilio, setDomicilio] = useState("");
  const [cp, setCP] = useState("");
  const [provincia, setProvincia] = useState("");
  const [localidad, setLocalidad] = useState("");
  const [telefono, setTelefono] = useState("");
   //Variables necesarias para poder usar /addStudent_Calification
   const [notamedia, setNotaMedia] = useState("");
   const [notaidioma, setNotaIdioma] = useState("");
   const [notamadurez, setNotaMadurez] = useState("");
   const [notacompetencia, setNotaCompetencia] = useState("");
   const [numfaltas, setNumFaltas] = useState("");
   const [notafaltas, setNotaFaltas] = useState("");
   const [notaglobal, setNotaGlobal] = useState("");
   const [observaciones2, setObservaciones2] = useState("");

   const [idioma, setIdioma] = useState("");
   
  
 // ----------------------------------------------------------------   USE EFFECTS
 // Llamar a las funciones
  useEffect(() => {
    GetAllIdioms(); // Llamada a la función
    GetAllStudents();
  }, []);


   //Metodo que compara el nombre del idioma del alumno introducido por el usuario con el idioma
 // en la BBDD para obtener el id del idioma de la BBDD y enviarlo a addStudent_Idiom para insertarlo
 // en la tabla idiomas_alumnos.
//  useEffect(() => {
//   // Cuando se actualiza la variable idiomaAlumno, buscar su correspondiente ididioma
//   if (idiomaalumno && data2.length > 0) {
//     // Si hay un valor en idiomaAlumno y hay datos disponibles en data2
//     const foundIdioma = data2.find(idioma => idioma.idioma === idiomaalumno);
//     // Utiliza el método find para buscar un idioma en data2 cuyo atributo idioma coincida con idiomaAlumno
//     if (foundIdioma) {
//       // Si se encuentra un idioma que coincida
//       setIdIdioma(foundIdioma.ididioma);
//       // Establece el ididioma correspondiente en el estado idiomaAlumnoId
//     } else {
//       // Si no se encuentra un idioma que coincida
//       setIdIdioma('');
//       // Establece el estado idiomaAlumnoId en vacío ('')
//     }
//   }
// }, [idiomaalumno, data2]);
// Este efecto se ejecutará cada vez que idiomaAlumno o data2 cambien

  //Metodo que compara el nombre del idioma del alumno introducido por el usuario con el idioma
 // en la BBDD para obtener el id del idioma de la BBDD y enviarlo a addStudent_Idiom para insertarlo
 // en la tabla idiomas_alumnos.

  // Este efecto se ejecutará cada vez que idiomaAlumno o data2 cambien

  // -----------------------------------------------------------------   GET ALLS

  // Recoge todos los datos de la tabla idiomas y los guarda en data2
  function GetAllIdioms() {
    fetch('/getAllIdioms') // Hacer una solicitud HTTP GET a '/getAllIdioms'
      .then(response => response.json()) // Convertir la respuesta a JSON
      .then(data => {
        setData2(data); // Establecer los datos obtenidos en el estado 'data2'
        console.log(data); // Mostrar el contenido en la consola
      })
      .catch(error => {
        console.error('Error fetching idioms data:', error);
        // Manejar errores de la solicitud
      });
  }
  // 
  function GetAllStudents() {
    fetch('/getAllStudents') 
      .then(response => response.json()) 
      .then(data => {
        setData(data); 
        // Verificar si hay datos en la respuesta
        if (data.length > 0) {
          // Obtener el último idalumno de la lista de estudiantes
          const ultimoIdAlumno = data[data.length - 1].idalumno;
          setIdAlumno(ultimoIdAlumno); 
          console.log(ultimoIdAlumno);
        }
      })
      .catch(error => {
        console.error('Error fetching student data:', error);
        // Manejar errores de la solicitud
      });
  }

  function IncrementIdAlumno(idalumno){
    setIdAlumno(idalumno + 1);
  }
// ----------------------------------------------------------------  ALUMNOS_VALORACIONES
const AddNewStudent_Calification = async (idalumno,notamedia,notaidioma,notamadurez,notacompetencia,numfaltas,notafaltas,notaglobal,observaciones2) => {
  try {
    const bodyParameters = {
      'idalumno': idalumno,
      'notamedia': notamedia,
      'notaidioma': notaidioma,
      'notamadurez': notamadurez,
      'notacompetencia': notacompetencia,
      'numfaltas': numfaltas,
      'notafaltas': notafaltas,
      'notaglobal': notaglobal,
      'observaciones2': observaciones2
    }

    const options = {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(bodyParameters)
    }
    const response = await fetch("/addStudent_Calification", options);

    if (!response.ok) {
      throw new Error('Error '); // Puedes personalizar el mensaje de error según tus necesidades
    }

    const jsonResponse = await response.json();
    console.log(JSON.stringify(jsonResponse));
    return jsonResponse;
  } catch (error) {
    console.error('Error:', error.message);
    // Aquí puedes actualizar el estado para mostrar un mensaje de error al usuario
  }
}

//FUNCTIONS ALUMNOS_DOMICILIO
function HandleNotaMediaChange(event){
  setNotaMedia(event.target.value);
}
function HandleNotaIdiomaChange(event){
  setNotaIdioma(event.target.value);
}
function HandleNotaMadurezChange(event){
  setNotaMadurez(event.target.value);
}
function HandleNotaCompetenciaChange(event){
  setNotaCompetencia(event.target.value);
}
function HandleNumFaltasChange(event){
  setNumFaltas(event.target.value);
}
function HandleNotaFaltasChange(event){
  setNotaFaltas(event.target.value);
}
function HandleNotaGlobalChange(event){
  setNotaGlobal(event.target.value);
}
function HandleObservaciones2Change(event){
  setObservaciones2(event.target.value);
}
// ----------------------------------------------------------------  ALUMNOS_DOMICILIO

const AddNewStudent_Adress = async (idalumno,domicilio,cp,provincia,localidad,telefono) => {
  try {
    const bodyParameters = {
      'idalumno': idalumno,
      'domicilio': domicilio,
      'cp': cp,
      'provincia': provincia,
      'localidad': localidad,
      'telefono': telefono
    }

    const options = {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(bodyParameters)
    }
    const response = await fetch("/addStudent_Adress", options);

    if (!response.ok) {
      throw new Error('Error '); // Puedes personalizar el mensaje de error según tus necesidades
    }

    const jsonResponse = await response.json();
    console.log(JSON.stringify(jsonResponse));
    return jsonResponse;
  } catch (error) {
    console.error('Error:', error.message);
    // Aquí puedes actualizar el estado para mostrar un mensaje de error al usuario
  }
}

//FUNCTIONS ALUMNOS_DOMICILIO
function HandleDomicilioChange(event){
  setDomicilio(event.target.value);
}
function HandleCPChange(event){
  setCP(event.target.value);
}
function HandleProvinciaChange(event){
  setProvincia(event.target.value);
}
function HandleLocalidadChange(event){
  setLocalidad(event.target.value);
}
function HandleTelefonoChange(event){
  setTelefono(event.target.value);
}
// ----------------------------------------------------------------  DOC_ALUMNOS
const AddNewStudent_Doc = async (idalumno,docalum,url) => {
  try {
    const bodyParameters = {
      'idalumno': idalumno,
      'docalum': docalum,
      'url': url
    }

    const options = {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(bodyParameters)
    }
    const response = await fetch("/addStudent_Doc", options);

    if (!response.ok) {
      throw new Error('Error '); // Puedes personalizar el mensaje de error según tus necesidades
    }

    const jsonResponse = await response.json();
    console.log(JSON.stringify(jsonResponse));
    return jsonResponse;
  } catch (error) {
    console.error('Error:', error.message);
    // Aquí puedes actualizar el estado para mostrar un mensaje de error al usuario
  }
}


//FUNCTIONS DOC_ALUMNOS
function HandleDocAlumChange(event){
  setDocAlum(event.target.value);
}
function HandleURLChange(event){
  setUrl(event.target.value);
}
  // ----------------------------------------------------------------  ESTUDIANTES_PREFERENCIAS
  const AddNewStudent_Preferences = async (idalumno,opcion1,opcion2,opcion3,fecha) => {
    try {
      const bodyParameters = {
        'idalumno': idalumno,
        'opcion1': opcion1,
        'opcion2': opcion2,
        'opcion3': opcion3,
        'fecha': fecha
      }
  
      const options = {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(bodyParameters)
      }
      const response = await fetch("/addStudent_Preferences", options);
  
      if (!response.ok) {
        throw new Error('Error '); // Puedes personalizar el mensaje de error según tus necesidades
      }
  
      const jsonResponse = await response.json();
      console.log(JSON.stringify(jsonResponse));
      return jsonResponse;
    } catch (error) {
      console.error('Error:', error.message);
      // Aquí puedes actualizar el estado para mostrar un mensaje de error al usuario
    }
  }


  //FUNCTIONS PREFERENCIAS_ESTUDIANTES
  function HandleOpcion1Change(event){
    setOpcion1(event.target.value);
  }
  function HandleOpcion2Change(event){
    setOpcion2(event.target.value);
  }
  function HandleOpcion3Change(event){
    setOpcion3(event.target.value);
  }
  function HandleFechaChange(event){
    setFecha(event.target.value);
  }
  // ----------------------------------------------------------------  ESTUDIANTES_IDIOMAS
  const AddNewStudent_Idiom = async (idalumno,ididioma,titulo) => {
    console.log(idalumno,ididioma,titulo);
    try {
      const bodyParameters = {
        'idalumno': idalumno,
        'ididioma': ididioma,
        'titulo': titulo
      }
  
      const options = {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(bodyParameters)
      }
  
      const response = await fetch("/addStudent_Idiom", options);
  
      if (!response.ok) {
        throw new Error('Error '); // Puedes personalizar el mensaje de error según tus necesidades
      }
  
      const jsonResponse = await response.json();
      console.log(JSON.stringify(jsonResponse));
      return jsonResponse;
    } catch (error) {
      console.error('Error:', error.message);
      // Aquí puedes actualizar el estado para mostrar un mensaje de error al usuario
    }
  }

  //FUNCTIONS IDIOMAS_ESTUDIANTES
  function HandleIdiomaChange(event){
    setIdiomaAlumno(event.target.value);
  }
  function HandleTituloChange(event){
    setTitulo(event.target.value);
  }


   //----------------------------------------------------------------  ESTUDIANTES (ALUMNOS)
  const AddNewStudent = async (nombre, sexo, dni, fechanacimiento, estadocurriculum, estadoadmision, emailinstituto,
    nacionalidad, carnetconducir, disponibilidad, numeroSS, situacionlaboral, nombretutorlegal,
    dnitutorlegal, especialidad, telalumno, telfamilia, email, observaciones, mesFCT) => {
    try {
      const bodyParameters = {
        'nombre': nombre,
        'sexo': sexo,
        'dni': dni,
        'fechanacimiento': fechanacimiento,
        'estadocurriculum': estadocurriculum,
        'estadoadmision': estadoadmision,
        'emailinstituto': emailinstituto,
        'nacionalidad': nacionalidad,
        'carnetconducir': carnetconducir,
        'disponibilidad': disponibilidad,
        'numeroSS': numeroSS,
        'situacionlaboral': situacionlaboral,
        'nombretutorlegal': nombretutorlegal,
        'dnitutorlegal': dnitutorlegal,
        'especialidad': especialidad,
        'telalumno': telalumno,
        'telfamilia': telfamilia,
        'email': email,
        'observaciones': observaciones,
        'mesFCT': mesFCT
      }
  
      const options = {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(bodyParameters)
      }
  
      const response = await fetch("/addStudent", options);
  
      if (!response.ok) {
        throw new Error('Error al agregar el estudiante'); // Puedes personalizar el mensaje de error según tus necesidades
      }
  
      const jsonResponse = await response.json();
      console.log(JSON.stringify(jsonResponse));
      return jsonResponse;
    } catch (error) {
      console.error('Error:', error.message);
      // Aquí puedes actualizar el estado para mostrar un mensaje de error al usuario
    }
  }

  //FUNCTIONS ESTUDIANTES
  function HandleNombreChange(event){
