  // --------------------------------------------------------------------------  EJECUCIONES
  function ButtonClickAddStudent(){
    AddNewStudent(nombre, sexo, dni, fechanacimiento, estadocurriculum, estadoadmision, emailinstituto,
        nacionalidad, carnetconducir, disponibilidad, numeroSS, situacionlaboral, nombretutorlegal,
        dnitutorlegal, especialidad, telalumno, telfamilia, email, observaciones, mesFCT); 
    AddNewStudent_Preferences(idalumno,opcion1,opcion2,opcion3,fecha);
    AddNewStudent_Calification (idalumno,notamedia,notaidioma,notamadurez,notacompetencia,numfaltas,notafaltas,notaglobal,observaciones2);
    // Como el idalumno para idiomas y docs es el anterior al nuevo alumno añadidio,
    // ajusto con esta funcion para sumar +1 al id antior y que la variable idalumno ahora valga
    // lo que la nueva idalumno del alumno nuevo.
    IncrementIdAlumno(idalumno);

    //GetAllStudents(); 
  }
  function ButtonClickAddStudent_Idiom(){
     //UpdateIdiomaId (idiomaalumno, data2);
     AddNewStudent_Idiom (idalumno,ididioma,titulo); 
  }
  function ButtonClickAddStudent_Doc(){
    AddNewStudent_Doc (idalumno,docalum,url);
  }
  function ButtonClickAddStudent_Adress(){
    AddNewStudent_Adress (idalumno,domicilio,cp,provincia,localidad,telefono); 
  }
  function ButtonClickAddStudent_Idioms() {
    // Iterar sobre los arrays idiomas y titulos para enviar cada par de datos
    for (let i = 0; i < idiomas.length; i++) {
        // Buscar el ID del idioma correspondiente en data2
        const foundIdioma = data2.find(item => item.idioma === idiomas[i]);
        let ididioma;
        if (foundIdioma) {
            // Si se encuentra el idioma en data2, asignar su ID correspondiente
            ididioma = foundIdioma.ididioma;
            console.log('Valor de ididioma:' + ididioma);
        } else {
            console.error(`Error: El idioma '${idiomas[i]}' no fue encontrado en la base de datos.`);
            continue; // Saltar a la próxima iteración si el idioma no fue encontrado
        }

        // Crear un objeto con los datos del nuevo idioma en cada iteración
        const nuevoIdioma = {
            idalumno: idalumno,
            ididioma: ididioma,
            titulo: titulos[i]
        };
        console.log('Valor del JSON' + nuevoIdioma);
        
        // Configurar las opciones para la solicitud fetch
        const options = {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(nuevoIdioma) // Convertir el objeto a JSON
        };

        // Realizar la solicitud fetch al servidor para cada par de datos
        fetch('/addStudent_Idiom', options)
            .then(response => {
                if (!response.ok) {
                    throw new Error('Error al insertar idioma');
                }
                return response.json();
            })
            .then(jsonResponse => {
                // Manejar la respuesta del servidor si es necesario
                console.log(jsonResponse);
                // Aquí puedes realizar alguna acción adicional si la inserción fue exitosa
            })
            .catch(error => {
                console.error('Error al insertar idioma:', error);
                // Aquí puedes mostrar un mensaje de error al usuario si la inserción falla
            });
    }
  }

   //--------------------------------------------------------------------------------------   Stack de opciones - Documentos, idiomas, domicilios
    // Definimos un estado llamado 'nombres' usando el hook useState.
    // Inicializamos el estado con un array que contiene un string vacío.
    const [idiomas, setIdiomas] = useState(['']);
    const [titulos, setTitulos] = useState(['']);

    // Esta función maneja los cambios en los campos de entrada de idiomas.
    // Recibe el índice del nombre que está siendo modificado y el nuevo valor.
    const handleIdiomasChange = (index, value) => {
      // Creamos una copia de la lista de nombres para no mutar el estado directamente.
      const newIdiomas = [...idiomas];
      // Actualizamos el nombre en la posición 'index' con el nuevo valor.
      newIdiomas[index] = value;
      // Actualizamos el estado con la nueva lista de nombres.
      setIdiomas(newIdiomas);
    };
    const handleTitulosChange = (index, value) => {
      // Creamos una copia de la lista de nombres para no mutar el estado directamente.
      const newTitulos = [...titulos];
      // Actualizamos el nombre en la posición 'index' con el nuevo valor.
      newTitulos[index] = value;
      // Actualizamos el estado con la nueva lista de nombres.
      setTitulos(newTitulos);
    };
  
    // Esta función se encarga de agregar un nuevo campo de entrada de nombre.
    const addIdiomasInput = () => {
      // Agregamos un nuevo elemento vacío al final de la lista de nombres.
      // Esto hará que se agregue un nuevo campo de entrada en el renderizado.
      setIdiomas([...idiomas, '']);
      setTitulos([...titulos, '']);
    };

  
  // Renderizado del componente ------------------------------------------------  HTML
  return (
    <div>
      <div>
         <h1> INSERTAR NUEVO IDIOMA: </h1>
          {idiomas.map((idioma, index) => ( 
            <div key={index}>
              <input type="text" value={idioma} onChange={(e) => handleIdiomasChange(index, e.target.value)}  placeholder="Idioma"/> {/* Cada campo de entrada está vinculado a su respectivo nombre en la lista */}
              <input type="text" value={titulos[index]} onChange={(e) => handleTitulosChange(index, e.target.value)}  placeholder="Titulo"/>
            </div>
          ))}
          <button onClick={addIdiomasInput}> + </button> {/* Un botón para agregar otro campo de entrada de nombre */}
          <button onClick={ButtonClickAddStudent_Idioms}> AÑADIR IDIOMAS </button>
        </div>
      <div>
