              <input type="text" value={notafaltas} id="title-input" onChange={HandleNotaFaltasChange} />
          </div>
          <div>
              <label htmlFor="title-input">Notas global:</label>
              <input type="text" value={notaglobal} id="title-input" onChange={HandleNotaGlobalChange} />
          </div>
          <div>
              <label htmlFor="title-input">Observaciones:</label>
              <input type="text" value={observaciones2} id="title-input" onChange={HandleObservaciones2Change} />
          </div>
          <button type="button" onClick={ButtonClickAddStudent}>Insert</button> {/* Botón para insertar nuevo alumno */}
      </form>
      <form>
          <h1>Insertar documento:</h1>
          <div>
              <label htmlFor="title-input">Nombre del documento / Tipo de documento:</label>
              <input type="text" value={docalum} id="title-input" onChange={HandleDocAlumChange} />
          </div>
          <div>
              <label htmlFor="title-input">URL del Documento:</label>
              <input type="text" value={url} id="title-input" onChange={HandleURLChange} />
          </div>
          <button type="button" onClick={ButtonClickAddStudent_Doc}>Insert</button> {/* Botón para insertar nuevo alumno */}
      </form>
      <form>
          <h1>Insertar idioma:</h1>
          <div>
              <label htmlFor="title-input">Idioma:</label>
              <input type="text" value={idiomaalumno} id="title-input" onChange={HandleIdiomaChange} />
