import React, { useState, useEffect } from 'react';
import '../styles.css'; // Importa el archivo de estilos CSS global

function UpdateStudentComponent() {
  const [dataStudents, setDataStudents] = useState([]); //Array con los datos de gf_alumnosfct

   // ----------------------------------------------------------------   USE EFFECTS
   useEffect(() => {
    GetAllIdioms(); 
    GetAllStudents();
    GetAllPreferences();
  }, []);
   // -----------------------------------------------------------------   GET ALLS
  // Aqui recogemos los datos de la tabla gf_alumnos y los almacenamos en un array de estudiantes.
  function GetAllStudents() {
    fetch('/getAllStudents') 
      .then(response => response.json()) 
      .then(dataStudents => {
        setDataStudents(dataStudents); 
      })
      .catch(error => {
        console.error('Error fetching student data:', error); // Manejar errores de la solicitud
      });
  }

   // Inicializamos el estado local "curriculumStatus" con el primer valor del arraylist
   const [curriculumStatus, setCurriculumStatus] = useState(datos[0]);

   // Función para manejar el cambio en el campo de entrada
   const HandleCurriculumStatusChange = (event) => {
    setCurriculumStatus(event.target.value); // Actualizamos el estado con el nuevo valor
  };

  return (
    <div className="input-field">
      <label htmlFor="curriculumStatus-input">Estado del Curriculum:</label>
      {/* Usamos el estado local "curriculumStatus" como el valor del campo */}
      <input
        type="text"
        value={curriculumStatus}
        id="curriculumStatus-input"
        onChange={HandleCurriculumStatusChange}
      />
    </div>
  );

}

export default UpdateStudentComponent;
