// Header.js
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import '../styles.css';

function Header() {
  const [submenuOpen, setSubmenuOpen] = useState(false);

  const toggleSubmenu = () => {
    setSubmenuOpen(!submenuOpen);
  };

  return (
    <header>
      <div className="container">
        <div className="button-container">
          <div className="button" onClick={toggleSubmenu}>
            Alumnos
            {submenuOpen && (
              <div className="submenu">
                <div><Link to="/addStudent" className="subOption">AÃ±adir nuevo alumno</Link></div>
                <div><Link to="/updateStudent" className="subOption">Administrar Alumnos</Link></div>
                <div><Link to="/getAllStudents" className="subOption">Mostrar Alumnos</Link></div>
              </div>
            )}
          </div>
          <div className="button" onClick={toggleSubmenu}>
            Empresas
            {submenuOpen && (
              <div className="submenu">
                <div><Link to="/" className="subOption">AÃ±adir nueva empresa</Link></div>
                <div><Link to="/" className="subOption">Administrar Alumnos</Link></div>
                <div><Link to="/" className="subOption">Mostrar Empresas</Link></div>
              </div>
            )}
          </div>
          <div className="button" onClick={toggleSubmenu}>
            GestiÃ³n de candidatos
            {submenuOpen && (
              <div className="submenu">
                <div><Link to="/" className="subOption">AÃ±adir nuevo candidato</Link></div>
                <div><Link to="/" className="subOption">Administrar Candidatos</Link></div>
                <div><Link to="/" className="subOption">Mostrar candidatos</Link></div>
              </div>
            )}
          </div>
          <div className="button"><Link to="/idioms" className="subOption">Idiomas</Link></div>
          <div className="button"><Link to="" className="subOption">Contactos</Link></div>
        </div>
      </div>
    </header>
  );
}

export default Header;
