import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom'; // -> Esto es un hook, nos permite acceder al objeto actual.

function GetCandidate() {
  const location = useLocation(); // Obtiene el objeto location
  const {candidateId2} = location.state || {}; // Obtén el idalumno del estado de 
  const [candidateData, setCandidateData] = useState([]); //Va a almacenar todos del estudiante
  const [candidateData2, setCandidateData2] = useState([]); //Va a almacenar todos del estudiante

/*
Si studentId recibe datos, es actualizado, navegamos hasta /getStudent
*/
useEffect(() => {
    const fetchData = async () => {
        await GetCandidateDataById(candidateId2);
        await GetCandidateDataByIdPlusAtributtes(candidateId2);
        console.log("Datos del candidato:", candidateData);
        console.log("Datos del candidato2:", candidateData2);
    };

    fetchData();
}, [candidateId2]);


/*
Solicitamos la peticion para que nos devuela los datos del / los alumnos
que coincidan en su nombre con lo solicitado por el usuario.
*/
const GetCandidateDataById = async (candidateId2) => {
    try {
        const bodyParameters = {
        'idcandidato': candidateId2
        };
        const options = {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(bodyParameters)
        };
        const response = await fetch("/getCandidateDataById", options);
        if (!response.ok) {
        throw new Error('Error en la solicitud');
        }
        const jsonResponse = await response.json();
        setCandidateData(jsonResponse);
    } catch (error) {
        console.error('Error:', error.message);
    }
};

/*
Solicitamos la peticion para que nos devuela los datos del / los alumnos
que coincidan en su nombre con lo solicitado por el usuario.
*/
const GetCandidateDataByIdPlusAtributtes = async (candidateId2) => {
    try {
        const bodyParameters = {
        'idcandidato': candidateId2
        };
        const options = {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(bodyParameters)
        };
        const response = await fetch("/getCandidateDataByIdPlusAtributtes", options);
        if (!response.ok) {
        throw new Error('Error en la solicitud');
        }
        const jsonResponse = await response.json();
        setCandidateData2(jsonResponse);
    } catch (error) {
        console.error('Error:', error.message);
    }
};

    return (
        <div className="results-container">
          {candidateData.map((candidate, index) => (
            <div key={index} className="candidate-card">
              <ul className="candidate-info-list">
                <li><strong>Nombre:</strong> {candidate.cnn}</li>
                {/* Agrega aquí más elementos según lo necesites */}
              </ul>
            </div>
          ))}
        </div>
      );
    }

export default GetCandidate;
