import React, { useState, useEffect } from 'react';

function AddPreferenceComponent() {
  // Definición de estados utilizando el hook useState
  const [data, setData] = useState([]); // Estado para almacenar datos obtenidos del servidor
  const [idpreference, setIdPreference] = useState(""); // Estado para el nombre de la nueva nota
  const [preference, setPreference] = useState(""); // Estado para el texto de la nueva nota
  // Efecto que se ejecuta solo una vez al montar el componente
  useEffect(() => {
    GetAllPreferences(); // Llamada a la función GetAllNotes
  }, []);

  // PETICION GET -- MOSTRAR PREFERENCIA
  function GetAllPreferences() {
    fetch('/getAllPreferences') // Hacer una solicitud HTTP GET a '/getAllPreferences'
      .then(response => response.json()) // Convertir la respuesta a JSON
      .then(data => {
        console.log("Datos obtenidos:", data); // Imprimir los datos obtenidos en la consola
        setData(data); // Establecer los datos obtenidos en el estado 'data'
      })
      .catch(error => {
        console.error("Error al obtener los datos:", error); // Manejar cualquier error que ocurra durante la solicitud
      });
  }


  // FUNCION SET ID PREFERENCIA
  function HandleIdPreferenceChange(event){
    setIdPreference(event.target.value); // Establecer el nuevo valor del estado 'ididioma'
  }

  // FUNCION SET PREFERENCIA
  function HandlePreferenceChange(event){
    setPreference(event.target.value); // Establecer el nuevo valor del estado 'idioma'
  }
  // BUTTON INSERTAR NUEVA PREFERENCIA
  function ButtonClickAdd(){
    AddNewPreference(preference); // Llamar a la función AddNewIidom con el idioma.
    GetAllPreferences(); // Actualizar la lista de idiomas después de agregar una nueva
  }
  // BUTTON BORRAR IDIOMA PREFERENCIA
  function ButtonClickDelete(){
    DeletePreference(idpreference); // Llamar a la función AddNewRecord con el nombre y texto
    GetAllPreferences(); // Actualizar la lista de notas después de agregar una nueva
  }
  // BUTTON ACTUALIZAR PREFERENCIA
  function ButtonClickUpdate(){
    UpdatePreference(idpreference,preference); // Llamar a la función AddNewRecord con el nombre y texto
    GetAllPreferences(); // Actualizar la lista de notas después de agregar una nueva
  }

  // BUTTON NUEVA PREFERENCIA -- PETICION POST
  const AddNewPreference = async (preference) => {
    const bodyParameters = {
      'preferencia': preference
    }
    const options = {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(bodyParameters)
    }
  
    const response = await fetch("/addPreferences", options); // Hacer una solicitud HTTP POST a '/addNote' con los datos de la nueva nota
    const jsonResponse = await response.json(); // Convertir la respuesta a JSON
    console.log(JSON.stringify(jsonResponse)); // Imprimir la respuesta JSON en la consola
    return jsonResponse; // Devolver la respuesta JSON
  }
  // BORRAR IDIOMA -- PETICION POST
  const DeletePreference = async (idpreference) => {
    const bodyParameters = {
      'idpreferencia': idpreference
    }
  
    const options = {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(bodyParameters)
    }
  
    const response = await fetch("/deletePreferences", options); // Hacer una solicitud HTTP POST a '/addNote' con los datos de la nueva nota
    const jsonResponse = await response.json(); // Convertir la respuesta a JSON
    console.log(JSON.stringify(jsonResponse)); // Imprimir la respuesta JSON en la consola
    return jsonResponse; // Devolver la respuesta JSON
  }
  // ACTUALIZAR PREFERENCIA -- PETICION POST
  const UpdatePreference = async (idpreference,preference) => {
    const bodyParameters = {
      'idpreferencia':idpreference,
      'preferencia': preference
    }
  
    const options = {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(bodyParameters)
    }
  
    const response = await fetch("/updatePreferences", options); // Hacer una solicitud HTTP POST a '/addNote' con los datos de la nueva nota
    const jsonResponse = await response.json(); // Convertir la respuesta a JSON
    console.log(JSON.stringify(jsonResponse)); // Imprimir la respuesta JSON en la consola
    return jsonResponse; // Devolver la respuesta JSON
  }

  // Renderizado del componente
  return (
    <div>
      <h1>Insertar nueva preferencia:</h1>
      <form>
          <div>
              <label htmlFor="title-input">Preferencia:</label>
              <input type="text" value={preference} id="title-input" onChange={HandlePreferenceChange} /> {/* Entrada de texto para el nombre de la nueva nota */}
          </div>
          <button type="button" onClick={ButtonClickAdd}>Insert</button> {/* Botón para insertar la nueva nota */}
      </form>
      <h1>Borrar preferencia:</h1>
      <form>
          <div>
              <label htmlFor="title-input">IdPreferencia:</label>
              <input type="text" value={idpreference} id="title-input" onChange={HandleIdPreferenceChange} /> {/* Entrada de texto para el nombre de la nueva nota */}
          </div>
          <button type="button" onClick={ButtonClickDelete}>Insert</button> {/* Botón para insertar la nueva nota */}
      </form>
      <h1>Actualizar nueva preferencia:</h1>
      <form>
          <div>
              <label htmlFor="title-input">IdPreferencia:</label>
              <input type="text" value={idpreference} id="title-input" onChange={HandleIdPreferenceChange} /> {/* Entrada de texto para el nombre de la nueva nota */}
          </div>
          <div>
              <label htmlFor="author-input">Preferencia:</label>
              <input type="text" value={preference} id="author-input" onChange={HandlePreferenceChange} /> {/* Entrada de texto para el contenido de la nueva nota */}
          </div>
          <button type="button" onClick={ButtonClickUpdate}>Insert</button> {/* Botón para insertar la nueva nota */}
      </form>
      {/* Bucle for */}
      {data.map(item => (
        <div key={item.id}>
          <h2>{item.idpreferencia}</h2> {/* Renderizado del nombre de la nota */}
          <p>{item.preferencia}</p> {/* Renderizado del contenido de la nota */}
        </div>
      ))}
    </div>
  );
}

export default AddPreferenceComponent;
