import React, { useState } from 'react';
import Main from './Main'; // Importa el componente Main
import AddStudent from './AddStudent'; // Importa el componente AddStudent
import GetAllStudents from './GetAllStudents'; // Importa el componente GetAllStudents
import '../styles.css';

function Body() {
  const [currentComponent, setCurrentComponent] = useState('main'); // Estado para controlar el componente actualmente mostrado

  // Función para cambiar el componente actual basado en el botón pulsado en el Header
  const changeComponent = (component) => {
    setCurrentComponent(component);
  };

  // Función para renderizar el componente actual basado en el estado
  const renderCurrentComponent = () => {
    switch (currentComponent) {
      case 'main':
        return <Main />;
      case 'addStudent':
        return <AddStudent />;
      case 'getAllStudents':
        return <GetAllStudents />;
      default:
        return null;
    }
  };

  return (
    <div className="body">
      {renderCurrentComponent()} {/* Renderiza el componente actual */}
    </div>
  );
}

export default Body;
