import React, { useState, useEffect } from 'react';
import '../styles.css'; // Importa el archivo de estilos CSS global

function UpdateStudentComponent() {
  const [dataStudents, setDataStudents] = useState([]); //Array con los datos de gf_alumnosfct

   // ----------------------------------------------------------------   USE EFFECTS
   useEffect(() => {
    GetAllIdioms(); 
    GetAllStudents();
    GetAllPreferences();
  }, []);

  // Aqui recogemos los datos de la tabla gf_alumnos y los almacenamos en un array de estudiantes.
  function GetAllStudents() {
    fetch('/getAllStudents') 
      .then(response => response.json()) 
      .then(dataStudents => {
        setDataStudents(dataStudents); 
      })
      .catch(error => {
        console.error('Error fetching student data:', error); // Manejar errores de la solicitud
      });
  }

}

export default UpdateStudentComponent;
