import React, { useState, useEffect } from 'react';
import '../styles.css'; // Importa el archivo de estilos CSS global

function SearchStudentComponent() {
  const [name, setName] = useState(""); // Va a almacenar el nombre que nos de el usuario
  const [studentData, setStudentData] = useState([]); // Array que va a contener los datos del usuario/usuario coincidentes con el nombre
  const [seeMore, setSeeMore] = useState(false); //Variable boolean con valor falso, se usa para permitir mostrar o no, los datos del estudiante.

  useEffect(() => {
    if (name) {
      GetStudentDataByName(name); 
    }
  }, [name]);
  /*
     Peticion que envia el nombre del usuario y recoge todos los datos de la tabla del usuario solicitado.
  */
  const GetStudentDataByName = async (name) => {
    try {
      const bodyParameters = {
        'nombre': name };
      const options = {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(bodyParameters)
      };
      const response = await fetch("/getStudentDataByName", options);
      if (!response.ok) {
        throw new Error('Error en la solicitud'); 
      }
      const jsonResponse = await response.json();
      setStudentData(jsonResponse);
    } catch (error) {
      console.error('Error:', error.message);
    }
  };
  // Permite alterar el valor de nombre en tiempo real por el usuario.
  const HandleNameChange = (event) => {
    const newValue = event.target.value;
    if (newValue.length === 0) {
      setSeeMore(seeMore);
    } else {
      setSeeMore(!seeMore);
    }
    setName(newValue);
  };
  // Boton que ejecuta la funcion que permite mostrar los datos del usuario/usuarios.
  // Si se pulsa el boton, deja de tener valor falso y muestra los datos.



  //Renderizado.
  return (
    <div>
      <h4>BUSCADOR DE ALUMNOS</h4>
      <input type="text"  value={name} id="name-input" onChange={HandleNameChange} />

      {seeMore && studentData.length > 0 && (
        <div>
          {studentData.map((student, index) => (
            <div key={index} className="student-card">
              <h2>{student.nombre}</h2>
              <ul className="student-info-list">
                <li><strong>Nombre:</strong> {student.nombre}</li>
                <li><strong>DNI:</strong> {student.dni}</li>
                <li><strong>Nacionalidad:</strong> {student.nacionalidad}</li>
                <li><strong>Fecha de nacimiento:</strong> {student.fechanacimiento}</li>
              </ul>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default SearchStudentComponent;
