import React, { useState, useEffect } from 'react';

function ExampleComponent() {
  // Definición de estados utilizando el hook useState
  const [data, setData] = useState([]); // Estado para almacenar datos obtenidos del servidor
  const [name, setName] = useState(""); // Estado para el nombre de la nueva nota
  const [text, setText] = useState(""); // Estado para el texto de la nueva nota

  // Efecto que se ejecuta solo una vez al montar el componente
  useEffect(() => {
    GetAllNotes(); // Llamada a la función GetAllNotes
  }, []);

  // Función para obtener todas las notas del servidor
  function GetAllNotes() {
    fetch('/getAllNotes') // Hacer una solicitud HTTP GET a '/getAllNotes'
      .then(response => response.json()) // Convertir la respuesta a JSON
      .then(data => setData(data)); // Establecer los datos obtenidos en el estado 'data'
      consolelog(response);
      consolelog(data);
  }

  // Función para manejar el cambio en el nombre de la nueva nota
  function HandleNameChange(event){
    setName(event.target.value); // Establecer el nuevo valor del estado 'name'
  }

  // Función para manejar el cambio en el texto de la nueva nota
  function HandleTextChange(event){
    setText(event.target.value); // Establecer el nuevo valor del estado 'text'
  }

  // Función para manejar el clic en el botón de inserción de nueva nota
  function ButtonClick(){
    AddNewRecord(name, text); // Llamar a la función AddNewRecord con el nombre y texto
    GetAllNotes(); // Actualizar la lista de notas después de agregar una nueva
  }

  // Función asincrónica para agregar un nuevo registro de nota
  const AddNewRecord = async (name, text) => {
    const bodyParameters = {
      'nombre': name,
      'texto': text
    }

    const options = {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(bodyParameters)
    }

    const response = await fetch("/addNote", options); // Hacer una solicitud HTTP POST a '/addNote' con los datos de la nueva nota
    const jsonResponse = await response.json(); // Convertir la respuesta a JSON
    console.log(JSON.stringify(jsonResponse)); // Imprimir la respuesta JSON en la consola
    return jsonResponse; // Devolver la respuesta JSON
  }

  // Renderizado del componente
  return (
    <div>
      <h1>Inserta nueva nota:</h1>
      <form>
          <div>
              <label htmlFor="title-input">Name:</label>
              <input type="text" value={name} id="title-input" onChange={HandleNameChange} /> {/* Entrada de texto para el nombre de la nueva nota */}
          </div>
          <div>
              <label htmlFor="author-input">Text:</label>
              <input type="text" value={text} id="author-input" onChange={HandleTextChange} /> {/* Entrada de texto para el contenido de la nueva nota */}
          </div>
          <button type="button" onClick={ButtonClick}>Insert</button> {/* Botón para insertar la nueva nota */}
      </form>
      {/* Mapeo de los datos de las notas y su renderizado */}
      {data.map(item => (
        <div key={item.id}>
          <h2>{item.nombre}</h2> {/* Renderizado del nombre de la nota */}
          <p>{item.texto}</p> {/* Renderizado del contenido de la nota */}
        </div>
      ))}
    </div>
  );
}

export default ExampleComponent;
