import React, { useState, useEffect } from 'react';

function AddStudentComponent() {
  // Definición de estados utilizando el hook useState
  const [data, setData] = useState([]); // Estado para almacenar datos obtenidos del servidor
  const [name, setName] = useState("");
  const [gender, setGender] = useState("");
  const [DNI, setDNI] = useState("");
  const [birthdate, setBirthdate] = useState("");
  const [curriculumStatus, setCurriculumStatus] = useState("");
  const [admissionStatus, setAddmissionStatus] = useState("");
  const [studiesEmail, setStudiesEmail] = useState("");
  const [nationality, setNationality] = useState("");
  const [drivingLicense, setDrivingLicense] = useState("");
  const [availability, setAvailability] = useState("");
  const [SSnumber, setSSNumber] = useState("");
  const [employmentSituation, setEmploymentSituation] = useState("");
  const [legalGuardianName, setLegalGuardianName] = useState("");
  const [legalGuardianDNI, setLegalGuardianDNI] = useState("");
  const [speciality, setSpeciality] = useState("");
  const [studentTelephone, setStudentTelephone] = useState("");
  const [familyTelephone, setFamilyTelephone] = useState("");
  const [email, setEmail] = useState("");
  const [observations, setObservations] = useState("");
  const [FCTmonth, setFCTMonth] = useState("");
  //Variables necesarias para poder usar /addStudent_Idiom
  const [data2, setData2] = useState([]);
  const [idiomaalumno, setIdiomaAlumno] = useState("");
  const [idiomId, setIdiomId] = useState("");
  const [studentId, setStudentId] = useState("");
  const [degree, setDegree] = useState("");
  //Variables necesarias para poder usar /addStudentPreferences
  const [option1, setOption1] = useState("");
  const [option2, setOption2] = useState("");
  const [option3, setOption3] = useState("");
  const [date, setDate] = useState("");
  //Variables necesariasa para poder usar /addStudent_Doc
  const [studentDocument, setStudentDocument] = useState("");
  const [URL, setURL] = useState("");
  //Variables necesarias para poder usar /addStudent_Adress
  const [adress, setAdress] = useState("");
  const [CP, setCP] = useState("");
  const [province, setProvince] = useState("");
  const [location, setLocation] = useState("");
  const [telephone, setTelephone] = useState("");
   //Variables necesarias para poder usar /addStudent_Calification
   const [averageGrade, setAverageGrade] = useState("");
   const [idiomGrade, setIdiomGrade] = useState("");
   const [maturityGrade, setMaturityGrade] = useState("");
   const [competentGrade, setCompetentGrade] = useState("");
   const [failuresNumber, setFailuresNumber] = useState("");
   const [failuresGrade, setFailuresGrade] = useState("");
   const [globalGrade, setGlobalGrade] = useState("");
   const [observations2, setObservations2] = useState("");

   const [document, setDocument] = useState("");
   
  
 // ----------------------------------------------------------------   USE EFFECTS
 // Llamar a las funciones
  useEffect(() => {
    GetAllIdioms(); // Llamada a la función
    GetAllStudents();
  }, []);


   //Metodo que compara el nombre del idioma del alumno introducido por el usuario con el idioma
 // en la BBDD para obtener el id del idioma de la BBDD y enviarlo a addStudent_Idiom para insertarlo
 // en la tabla idiomas_alumnos.
//  useEffect(() => {
//   // Cuando se actualiza la variable idiomaAlumno, buscar su correspondiente ididioma
//   if (idiomaalumno && data2.length > 0) {
//     // Si hay un valor en idiomaAlumno y hay datos disponibles en data2
//     const foundIdioma = data2.find(idioma => idioma.idioma === idiomaalumno);
//     // Utiliza el método find para buscar un idioma en data2 cuyo atributo idioma coincida con idiomaAlumno
//     if (foundIdioma) {
//       // Si se encuentra un idioma que coincida
//       setIdIdioma(foundIdioma.ididioma);
//       // Establece el ididioma correspondiente en el estado idiomaAlumnoId
//     } else {
//       // Si no se encuentra un idioma que coincida
//       setIdIdioma('');
//       // Establece el estado idiomaAlumnoId en vacío ('')
//     }
//   }
// }, [idiomaalumno, data2]);
// Este efecto se ejecutará cada vez que idiomaAlumno o data2 cambien

  //Metodo que compara el nombre del idioma del alumno introducido por el usuario con el idioma
 // en la BBDD para obtener el id del idioma de la BBDD y enviarlo a addStudent_Idiom para insertarlo
 // en la tabla idiomas_alumnos.

  // Este efecto se ejecutará cada vez que idiomaAlumno o data2 cambien

  // -----------------------------------------------------------------   GET ALLS

  // Recoge todos los datos de la tabla idiomas y los guarda en data2
  function GetAllIdioms() {
    fetch('/getAllIdioms') // Hacer una solicitud HTTP GET a '/getAllIdioms'
      .then(response => response.json()) // Convertir la respuesta a JSON
      .then(data => {
        setData2(data); // Establecer los datos obtenidos en el estado 'data2'
        console.log(data); // Mostrar el contenido en la consola
      })
      .catch(error => {
        console.error('Error fetching idioms data:', error);
        // Manejar errores de la solicitud
      });
  }
  // 
  function GetAllStudents() {
    fetch('/getAllStudents') 
      .then(response => response.json()) 
      .then(data => {
        setData(data); 
        // Verificar si hay datos en la respuesta
        if (data.length > 0) {
          // Obtener el último idalumno de la lista de estudiantes
          const ultimoIdAlumno = data[data.length - 1].idalumno;
          setIdAlumno(ultimoIdAlumno); 
          console.log(ultimoIdAlumno);
        }
      })
      .catch(error => {
        console.error('Error fetching student data:', error);
        // Manejar errores de la solicitud
      });
  }

  function IncrementIdAlumno(idalumno){
    setIdAlumno(idalumno + 1);
  }
// ----------------------------------------------------------------  ALUMNOS_VALORACIONES
const AddNewStudent_Calification = async (idalumno,notamedia,notaidioma,notamadurez,notacompetencia,numfaltas,notafaltas,notaglobal,observaciones2) => {
  try {
    const bodyParameters = {
      'idalumno': idalumno,
      'notamedia': notamedia,
      'notaidioma': notaidioma,
      'notamadurez': notamadurez,
      'notacompetencia': notacompetencia,
      'numfaltas': numfaltas,
      'notafaltas': notafaltas,
      'notaglobal': notaglobal,
      'observaciones2': observaciones2
    }

    const options = {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(bodyParameters)
    }
    const response = await fetch("/addStudent_Calification", options);

    if (!response.ok) {
      throw new Error('Error '); // Puedes personalizar el mensaje de error según tus necesidades
    }

    const jsonResponse = await response.json();
    console.log(JSON.stringify(jsonResponse));
    return jsonResponse;
  } catch (error) {
    console.error('Error:', error.message);
    // Aquí puedes actualizar el estado para mostrar un mensaje de error al usuario
  }
}

//FUNCTIONS ALUMNOS_VALORACION
function HandleNotaMediaChange(event){
  setNotaMedia(event.target.value);
}
function HandleNotaIdiomaChange(event){
  setNotaIdioma(event.target.value);
}
function HandleNotaMadurezChange(event){
  setNotaMadurez(event.target.value);
}
function HandleNotaCompetenciaChange(event){
  setNotaCompetencia(event.target.value);
}
function HandleNumFaltasChange(event){
  setNumFaltas(event.target.value);
}
function HandleNotaFaltasChange(event){
  setNotaFaltas(event.target.value);
}
function HandleNotaGlobalChange(event){
  setNotaGlobal(event.target.value);
}
function HandleObservaciones2Change(event){
  setObservaciones2(event.target.value);
}
  // ----------------------------------------------------------------  ESTUDIANTES_PREFERENCIAS
  const AddNewStudent_Preferences = async (idalumno,opcion1,opcion2,opcion3,fecha) => {
    try {
      const bodyParameters = {
        'idalumno': idalumno,
        'opcion1': opcion1,
        'opcion2': opcion2,
        'opcion3': opcion3,
        'fecha': fecha
      }
  
      const options = {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(bodyParameters)
      }
      const response = await fetch("/addStudent_Preferences", options);
  
      if (!response.ok) {
        throw new Error('Error '); // Puedes personalizar el mensaje de error según tus necesidades
      }
  
      const jsonResponse = await response.json();
      console.log(JSON.stringify(jsonResponse));
      return jsonResponse;
    } catch (error) {
      console.error('Error:', error.message);
      // Aquí puedes actualizar el estado para mostrar un mensaje de error al usuario
    }
  }


  //FUNCTIONS PREFERENCIAS_ESTUDIANTES
  function HandleOpcion1Change(event){
    setOpcion1(event.target.value);
  }
  function HandleOpcion2Change(event){
    setOpcion2(event.target.value);
  }
  function HandleOpcion3Change(event){
    setOpcion3(event.target.value);
  }
  function HandleFechaChange(event){
    setFecha(event.target.value);
  }
  //----------------------------------------------------------------  ESTUDIANTES (ALUMNOS)
  const AddNewStudent = async (nombre, sexo, dni, fechanacimiento, estadocurriculum, estadoadmision, emailinstituto,
    nacionalidad, carnetconducir, disponibilidad, numeroSS, situacionlaboral, nombretutorlegal,
    dnitutorlegal, especialidad, telalumno, telfamilia, email, observaciones, mesFCT) => {
    try {
      const bodyParameters = {
        'nombre': nombre,
        'sexo': sexo,
        'dni': dni,
        'fechanacimiento': fechanacimiento,
        'estadocurriculum': estadocurriculum,
        'estadoadmision': estadoadmision,
        'emailinstituto': emailinstituto,
        'nacionalidad': nacionalidad,
        'carnetconducir': carnetconducir,
        'disponibilidad': disponibilidad,
        'numeroSS': numeroSS,
        'situacionlaboral': situacionlaboral,
        'nombretutorlegal': nombretutorlegal,
        'dnitutorlegal': dnitutorlegal,
        'especialidad': especialidad,
        'telalumno': telalumno,
        'telfamilia': telfamilia,
        'email': email,
        'observaciones': observaciones,
        'mesFCT': mesFCT
      }
  
      const options = {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(bodyParameters)
      }
  
      const response = await fetch("/addStudent", options);
  
      if (!response.ok) {
        throw new Error('Error al agregar el estudiante'); // Puedes personalizar el mensaje de error según tus necesidades
      }
  
      const jsonResponse = await response.json();
      console.log(JSON.stringify(jsonResponse));
      return jsonResponse;
    } catch (error) {
      console.error('Error:', error.message);
      // Aquí puedes actualizar el estado para mostrar un mensaje de error al usuario
    }
  }

  //FUNCTIONS ESTUDIANTES
  function HandleNombreChange(event){
    setNombre(event.target.value);
  }
  function HandleSexoChange(event) {
    setSexo(event.target.value);
  }

  function HandleDniChange(event) {
      setDni(event.target.value);
  }

  function HandleFechanacimientoChange(event) {
      setFechanacimiento(event.target.value);
  }

  function HandleEstadocurriculumChange(event) {
      setEstadocurriculum(event.target.value);
  }

  function HandleEstadoadmisionChange(event) {
      setEstadoadmision(event.target.value);
  }

  function HandleEmailinstitutoChange(event) {
      setEmailinstituto(event.target.value);
  }

  function HandleNacionalidadChange(event) {
      setNacionalidad(event.target.value);
  }

  function HandleCarnetconducirChange(event) {
      setCarnetconducir(event.target.value);
  }

  function HandleDisponibilidadChange(event) {
      setDisponibilidad(event.target.value);
  }

  function HandleNumeroSSChange(event) {
      setNumeroSS(event.target.value);
  }

  function HandleSituacionlaboralChange(event) {
      setSituacionlaboral(event.target.value);
  }

  function HandleNombretutorlegalChange(event) {
      setNombretutorlegal(event.target.value);
  }

  function HandleDnitutorlegalChange(event) {
      setDnitutorlegal(event.target.value);
  }

  function HandleEspecialidadChange(event) {
      setEspecialidad(event.target.value);
  }

  function HandleTelalumnoChange(event) {
      setTelalumno(event.target.value);
  }

  function HandleTelfamiliaChange(event) {
      setTelfamilia(event.target.value);
  }

  function HandleEmailChange(event) {
      setEmail(event.target.value);
  }

  function HandleObservacionesChange(event) {
      setObservaciones(event.target.value);
  }

  function HandleMesFCTChange(event) {
      setMesFCT(event.target.value);
  }


  
  // --------------------------------------------------------------------------  EJECUCIONES
  function ButtonClickAddStudent(){
    AddNewStudent(nombre, sexo, dni, fechanacimiento, estadocurriculum, estadoadmision, emailinstituto,
        nacionalidad, carnetconducir, disponibilidad, numeroSS, situacionlaboral, nombretutorlegal,
        dnitutorlegal, especialidad, telalumno, telfamilia, email, observaciones, mesFCT); 
    AddNewStudent_Preferences(idalumno,opcion1,opcion2,opcion3,fecha);
    AddNewStudent_Calification (idalumno,notamedia,notaidioma,notamadurez,notacompetencia,numfaltas,notafaltas,notaglobal,observaciones2);
    // Como el idalumno para idiomas y docs es el anterior al nuevo alumno añadidio,
    // ajusto con esta funcion para sumar +1 al id antior y que la variable idalumno ahora valga
    // lo que la nueva idalumno del alumno nuevo.
    AddStudent_Idioms(data2,idalumno,idiomas,titulos);
    AddStudent_Documents(idalumno,documents,urls);
    AddStudent_Adresses (idalumno,adressParametters);
    IncrementIdAlumno(idalumno);

    //GetAllStudents(); 
  }

  function AddStudent_Idioms() {
    // Iterar sobre los arrays idiomas y titulos para enviar cada par de datos
    for (let i = 0; i < idiomas.length; i++) {
        // Buscar el ID del idioma correspondiente en data2
        const foundIdioma = data2.find(item => item.idioma === idiomas[i]);
        let ididioma;
        if (foundIdioma) {
            // Si se encuentra el idioma en data2, asignar su ID correspondiente
            ididioma = foundIdioma.ididioma;
            console.log('Valor de ididioma:' + ididioma);
        } else {
            console.error(`Error: El idioma '${idiomas[i]}' no fue encontrado en la base de datos.`);
            continue; // Saltar a la próxima iteración si el idioma no fue encontrado
        }

        // Crear un objeto con los datos del nuevo idioma en cada iteración
        const bodyParameters = {
            idalumno: idalumno,
            ididioma: ididioma,
            titulo: titulos[i]
        };
        
        // Configurar las opciones para la solicitud fetch
        const options = {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(bodyParameters) // Convertir el objeto a JSON
        };

        // Realizar la solicitud fetch al servidor para cada par de datos
        fetch('/addStudent_Idiom', options)
            .then(response => {
                if (!response.ok) {
                    throw new Error('Error al insertar idioma');
                }
                return response.json();
            })
            .then(jsonResponse => {
                // Manejar la respuesta del servidor si es necesario
                console.log(jsonResponse);
                // Aquí puedes realizar alguna acción adicional si la inserción fue exitosa
            })
            .catch(error => {
                console.error('Error al insertar idioma:', error);
                // Aquí puedes mostrar un mensaje de error al usuario si la inserción falla
            });
    }
  }

  function AddStudent_Documents() {
    // Iterar sobre los arrays idiomas y titulos para enviar cada par de datos
    for (let i = 0; i < documents.length; i++) {
        // Crear un objeto con los datos del nuevo documento en cada iteración
        const bodyParameters = {
            idalumno: idalumno,
            docalum: documents[i],
            url: urls[i]
        };
        console.log(bodyParameters);
        // Configurar las opciones para la solicitud fetch
        const options = {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(bodyParameters) // Convertir el objeto a JSON
        };

        // Realizar la solicitud fetch al servidor para cada par de datos
        fetch('/addStudent_Doc', options)
            .then(response => {
                if (!response.ok) {
                    throw new Error('Error al insertar documento');
                }
                return response.json();
            })
            .then(jsonResponse => {
                // Manejar la respuesta del servidor si es necesario
                console.log(jsonResponse);
                // Aquí puedes realizar alguna acción adicional si la inserción fue exitosa
            })
            .catch(error => {
                console.error('Error al insertar idioma:', error);
                // Aquí puedes mostrar un mensaje de error al usuario si la inserción falla
            });
    }
  }
  function AddStudent_Adresses() {
    // Iterar sobre los arrays idiomas y titulos para enviar cada par de datos
    for (let i = 0; i < adressParametters.length; i++) {
        // Crear un objeto con los datos del nuevo documento en cada iteración
        const bodyParameters = {
            idalumno: idalumno,
            domicilio: adressParametters[i].adress,
            cp: adressParametters[i].cp,
            provincia: adressParametters[i].province,
            localidad: adressParametters[i].location,
            telefono: adressParametters[i].telephone
        };
        console.log(bodyParameters);
        // Configurar las opciones para la solicitud fetch
        const options = {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(bodyParameters) // Convertir el objeto a JSON
        };

        // Realizar la solicitud fetch al servidor para cada par de datos
        fetch('/addStudent_Adress', options)
            .then(response => {
                if (!response.ok) {
                    throw new Error('Error al insertar direccion');
                }
                return response.json();
            })
            .then(jsonResponse => {
                // Manejar la respuesta del servidor si es necesario
                console.log(jsonResponse);
                // Aquí puedes realizar alguna acción adicional si la inserción fue exitosa
            })
            .catch(error => {
                console.error('Error al insertar direccion:', error);
                // Aquí puedes mostrar un mensaje de error al usuario si la inserción falla
            });
    }
  }
   //--------------------------------------------------------------------------------------   IDIOMAS
    // Definimos un estado llamado 'nombres' usando el hook useState.
    // Inicializamos el estado con un array que contiene un string vacío.
    const [idiomas, setIdiomas] = useState(['']);
    const [titulos, setTitulos] = useState(['']);

    // Esta función maneja los cambios en los campos de entrada de idiomas.
    // Recibe el índice del nombre que está siendo modificado y el nuevo valor.
    const handleIdiomasChange = (index, value) => {
      // Creamos una copia de la lista de nombres para no mutar el estado directamente.
      const newIdiomas = [...idiomas];
      // Actualizamos el nombre en la posición 'index' con el nuevo valor.
      newIdiomas[index] = value;
      // Actualizamos el estado con la nueva lista de nombres.
      setIdiomas(newIdiomas);
    };
    const handleTitulosChange = (index, value) => {
      // Creamos una copia de la lista de nombres para no mutar el estado directamente.
      const newTitulos = [...titulos];
      // Actualizamos el nombre en la posición 'index' con el nuevo valor.
      newTitulos[index] = value;
      // Actualizamos el estado con la nueva lista de nombres.
      setTitulos(newTitulos);
    };
  
    // Esta función se encarga de agregar un nuevo campo de entrada de nombre.
    const addIdiomasInput = () => {
      // Agregamos un nuevo elemento vacío al final de la lista de nombres.
      // Esto hará que se agregue un nuevo campo de entrada en el renderizado.
      setIdiomas([...idiomas, '']);
      setTitulos([...titulos, '']);
    };
    // -------------------------------------------------------------------------------------------  DOCUMENTOS

    const [documents, setDocuments] = useState(['']);
    const [urls, setUrls] = useState(['']);

    const handleDocumentChange = (index, value) => {
      const newDocument = [...documents];
      newDocument[index] = value;
      setDocuments(newDocument);
    };
    const handleUrlChange = (index, value) => {
      const newUrl = [...urls];
      newUrl[index] = value;
      setUrls(newUrl);
    };

    const addDocumentsInput = () => {
      setDocuments([...documents, '']);
      setUrls([...urls, '']);
    };
    //---------------------------------------------------------------------------------------------- DOMICILIOS
    const [adressParametters, setAdressParametters] = useState([{adress: '', cp: '', province: '',location: '', telephone: ''}])
    
    const handleAdressChange = (index, value) => {
      const newAdressParametters = [...adressParametters];
      newAdressParametters[index].adress = value;
      setAdressParametters(newAdressParametters);
    };

    const handleCpChange = (index, value) => {
      const newAdressParametters = [...adressParametters];
      newAdressParametters[index].cp = value;
      setAdressParametters(newAdressParametters);
    };
    const handleProvinceChange = (index, value) => {
      const newAdressParametters = [...adressParametters];
      newAdressParametters[index].province = value;
      setAdressParametters(newAdressParametters);
    };

    const handleLocationChange = (index, value) => {
      const newAdressParametters = [...adressParametters];
      newAdressParametters[index].location = value;
      setAdressParametters(newAdressParametters);
    };

    const handleTelephoneChange = (index, value) => {
      const newAdressParametters = [...adressParametters];
      newAdressParametters[index].telephone = value;
      setAdressParametters(newAdressParametters);
    };

    const addAdressInput = () => {
      setAdressParametters([...adressParametters, {adress: '', cp: '', province: '', location: '', telephone: ''}]);
    };
    
  
  // Renderizado del componente ------------------------------------------------  HTML
  return (
    <div>
      <form>
          <h4>AÑADIR ALUMNO:</h4>
          <div>
              <label htmlFor="title-input">Fecha hoy:</label>
              <input type="text" value={fecha} id="title-input" onChange={HandleFechaChange} />
          </div>
          <div>
              <label htmlFor="title-input">Primera opcion estudiante:</label>
              <input type="text" value={opcion1} id="title-input" onChange={HandleOpcion1Change} />
          </div>
          <div>
              <label htmlFor="title-input">Segunda opcion estudiante:</label>
              <input type="text" value={opcion2} id="title-input" onChange={HandleOpcion2Change} />
          </div>
          <div>
              <label htmlFor="title-input">Tercera opcion estudiante:</label>
              <input type="text" value={opcion3} id="title-input" onChange={HandleOpcion3Change} />
          </div>
          <div>
              <label htmlFor="title-input">Alumno:</label>
              <input type="text" value={nombre} id="title-input" onChange={HandleNombreChange} />
          </div>
          <div>
              <label htmlFor="sexo-input">Sexo:</label>
              <input type="text" value={sexo} id="sexo-input" onChange={HandleSexoChange} />
          </div>

          <div>
              <label htmlFor="dni-input">DNI:</label>
              <input type="text" value={dni} id="dni-input" onChange={HandleDniChange} />
          </div>

          <div>
              <label htmlFor="fechanacimiento-input">Fecha de Nacimiento:</label>
              <input type="text" value={fechanacimiento} id="fechanacimiento-input" onChange={HandleFechanacimientoChange} />
          </div>

          <div>
              <label htmlFor="estadocurriculum-input">Estado del Curriculum:</label>
              <input type="text" value={estadocurriculum} id="estadocurriculum-input" onChange={HandleEstadocurriculumChange} />
          </div>

          <div>
              <label htmlFor="estadoadmision-input">Estado de Admisión:</label>
              <input type="text" value={estadoadmision} id="estadoadmision-input" onChange={HandleEstadoadmisionChange} />
          </div>

          <div>
              <label htmlFor="emailinstituto-input">Email del Instituto:</label>
              <input type="text" value={emailinstituto} id="emailinstituto-input" onChange={HandleEmailinstitutoChange} />
          </div>

          <div>
              <label htmlFor="nacionalidad-input">Nacionalidad:</label>
              <input type="text" value={nacionalidad} id="nacionalidad-input" onChange={HandleNacionalidadChange} />
          </div>

          <div>
              <label htmlFor="carnetconducir-input">Carnet de Conducir:</label>
              <input type="text" value={carnetconducir} id="carnetconducir-input" onChange={HandleCarnetconducirChange} />
          </div>

          <div>
              <label htmlFor="disponibilidad-input">Disponibilidad:</label>
              <input type="text" value={disponibilidad} id="disponibilidad-input" onChange={HandleDisponibilidadChange} />
          </div>

          <div>
              <label htmlFor="numeroSS-input">Número de Seguridad Social:</label>
              <input type="text" value={numeroSS} id="numeroSS-input" onChange={HandleNumeroSSChange} />
          </div>

          <div>
              <label htmlFor="situacionlaboral-input">Situación Laboral:</label>
              <input type="text" value={situacionlaboral} id="situacionlaboral-input" onChange={HandleSituacionlaboralChange} />
          </div>

          <div>
              <label htmlFor="nombretutorlegal-input">Nombre del Tutor Legal:</label>
              <input type="text" value={nombretutorlegal} id="nombretutorlegal-input" onChange={HandleNombretutorlegalChange} />
          </div>

          <div>
              <label htmlFor="dnitutorlegal-input">DNI del Tutor Legal:</label>
              <input type="text" value={dnitutorlegal} id="dnitutorlegal-input" onChange={HandleDnitutorlegalChange} />
          </div>

          <div>
              <label htmlFor="especialidad-input">Especialidad:</label>
              <input type="text" value={especialidad} id="especialidad-input" onChange={HandleEspecialidadChange} />
          </div>

          <div>
              <label htmlFor="telalumno-input">Teléfono del Alumno:</label>
              <input type="text" value={telalumno} id="telalumno-input" onChange={HandleTelalumnoChange} />
          </div>

          <div>
              <label htmlFor="telfamilia-input">Teléfono de la Familia:</label>
              <input type="text" value={telfamilia} id="telfamilia-input" onChange={HandleTelfamiliaChange} />
          </div>

          <div>
              <label htmlFor="email-input">Email personal:</label>
              <input type="text" value={email} id="email-input" onChange={HandleEmailChange} />
          </div>

          <div>
              <label htmlFor="observaciones-input">Observaciones:</label>
              <input type="text" value={observaciones} id="observaciones-input" onChange={HandleObservacionesChange} />
          </div>

          <div>
              <label htmlFor="mesFCT-input">Mes FCT:</label>
              <input type="text" value={mesFCT} id="mesFCT-input" onChange={HandleMesFCTChange} />
          </div>
      </form>
      <form>
          <h4>AÑADIR VALORACIÓN:</h4>
          <div>
              <label htmlFor="title-input">Nota media:</label>
              <input type="text" value={notamedia} id="title-input" onChange={HandleNotaMediaChange} />
          </div>
          <div>
              <label htmlFor="title-input">Nota idioma:</label>
              <input type="text" value={notaidioma} id="title-input" onChange={HandleNotaIdiomaChange} />
          </div>
          <div>
              <label htmlFor="title-input">Nota madurez:</label>
              <input type="text" value={notamadurez} id="title-input" onChange={HandleNotaMadurezChange} />
          </div>
          <div>
              <label htmlFor="title-input">Nota competencia:</label>
              <input type="text" value={notacompetencia} id="title-input" onChange={HandleNotaCompetenciaChange} />
          </div>
          <div>
              <label htmlFor="title-input">Total de faltas:</label>
              <input type="text" value={numfaltas} id="title-input" onChange={HandleNumFaltasChange} />
          </div>
          <div>
              <label htmlFor="title-input">Notas faltas:</label>
              <input type="text" value={notafaltas} id="title-input" onChange={HandleNotaFaltasChange} />
          </div>
          <div>
              <label htmlFor="title-input">Notas global:</label>
              <input type="text" value={notaglobal} id="title-input" onChange={HandleNotaGlobalChange} />
          </div>
          <div>
              <label htmlFor="title-input">Observaciones:</label>
              <input type="text" value={observaciones2} id="title-input" onChange={HandleObservaciones2Change} />
          </div>
      </form>
      <div>
         <h4> INSERTAR NUEVO IDIOMA: </h4>
          {idiomas.map((idioma, index) => ( 
            <div key={index}>
              <input type="text" value={idioma} onChange={(e) => handleIdiomasChange(index, e.target.value)}  placeholder="Idioma"/> {/* Cada campo de entrada está vinculado a su respectivo nombre en la lista */}
              <input type="text" value={titulos[index]} onChange={(e) => handleTitulosChange(index, e.target.value)}  placeholder="Titulo"/>
            </div>
          ))}
          <button onClick={addIdiomasInput}> + </button> {/* Un botón para agregar otro campo de entrada de nombre */}
        </div>
      <div>
        < h4> INSERTAR NUEVO DOCUMENTO: </h4>
        {documents.map((document, index) => ( 
            <div key={index}>
              <input type="text" value={document} onChange={(e) => handleDocumentChange(index, e.target.value)}  placeholder="Documento"/> {/* Cada campo de entrada está vinculado a su respectivo nombre en la lista */}
              <input type="text" value={urls[index]} onChange={(e) => handleUrlChange(index, e.target.value)}  placeholder="URL"/>
            </div>
          ))}
          <button onClick={addDocumentsInput}> + </button> {/* Un botón para agregar otro campo de entrada de nombre */}
      </div>
      <div>
        <h4> INSERTAR NUEVO DOMICILIO: </h4>
        {adressParametters.map((adressParametters, index) => ( 
            <div key={index}>
              <input type="text" value={adressParametters.adress} onChange={(e) => handleAdressChange(index, e.target.value)}  placeholder="Dirección"/> {/* Cada campo de entrada está vinculado a su respectivo nombre en la lista */}
              <input type="text" value={adressParametters.cp} onChange={(e) => handleCpChange(index, e.target.value)}  placeholder="CP"/>
              <input type="text" value={adressParametters.province} onChange={(e) => handleProvinceChange(index, e.target.value)}  placeholder="Provincia"/>
              <input type="text" value={adressParametters.location} onChange={(e) => handleLocationChange(index, e.target.value)}  placeholder="Localidad"/>
              <input type="text" value={adressParametters.telephone} onChange={(e) => handleTelephoneChange(index, e.target.value)}  placeholder="Teléfono personal"/>
            </div>
          ))}
          <button onClick={addAdressInput}> + </button> {/* Un botón para agregar otro campo de entrada de nombre */}
      </div>
      <button type="button" onClick={ButtonClickAddStudent}> AÑADIR ALUMNO </button> {/* Botón para insertar nuevo alumno */}
    </div>
  );
}

export default AddStudentComponent;
