import React from 'react';
import { Link } from 'react-router-dom'; // Importa Link desde react-router-dom
import '../styles.css'; // Importa el archivo de estilos CSS

function Body() {
  return (
    <div className="body">
      {/* Botón principal "Alumnos" */}
      <div className="button" onClick={toggleSubmenu}>
        Alumnos
        {/* Condicional para mostrar el submenú si está abierto */}
        {submenuOpen && (
          <div className="submenu">
            {/* Enlaces del submenú */}
            <Link to="/addStudent">Administrar Alumnos</Link>
            <Link to="/">Mostrar Alumnos</Link>
          </div>
        )}
      </div>
      <Link to="" className="button">Empresas</Link>
      <Link to="" className="button">Gestión de Candidatos</Link>
      <Link to="/idioms" className="button">Idiomas</Link>
      <Link to="" className="button">Documentos Alumnos</Link>
      <Link to="" className="button">Documentos Empresas</Link>
      <Link to="" className="button">Contactos</Link>
    </div>
  );
}

export default Body;
