import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import GetStudent from './GetStudent'; // Ajusta la ruta de importación según la ubicación real del archivo
import '../styles.css';

function SearchStudentComponent() {
  const [name, setName] = useState("");
  const [studentData, setStudentData] = useState([]);
  const [seeMore, setSeeMore] = useState(false);
  const [selectedStudentId, setSelectedStudentId] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    if (name) {
      GetStudentDataByName(name);
    } else {
      setStudentData([]);
      setSeeMore(false);
    }
  }, [name]);

  const GetStudentDataByName = async (name) => {
    try {
      const bodyParameters = {
        'nombre': name
      };
      const options = {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(bodyParameters)
      };
      const response = await fetch("/getStudentDataByName", options);
      if (!response.ok) {
        throw new Error('Error en la solicitud');
      }
      const jsonResponse = await response.json();
      setStudentData(jsonResponse);
      setSeeMore(true);
    } catch (error) {
      console.error('Error:', error.message);
    }
  };

  const HandleNameChange = (event) => {
    setName(event.target.value);
  };

  const handleStudentSelect = (idalumno) => {
    console.log("Selected student id:", idalumno);
    setSelectedStudentId(idalumno);
    navigate(`/getStudent`); // Navega al componente GetStudent
  };

  return (
    <div className="search-container">
      <h4>BUSCADOR DE ALUMNOS</h4>
      <input 
        type="text" 
        value={name} 
        id="name-input" 
        onChange={HandleNameChange} 
        placeholder="Ejemplo: Lucía" 
        className="search-input"
      />
      
      {seeMore && studentData.length > 0 && (
        <div className="results-container">
          {studentData.map((student, index) => (
            <Link to={`/getStudent`} className="student-link" key={index} onClick={() => handleStudentSelect(student.idalumno)}>
              <div className="student-card">
                <ul className="student-info-list">
                  <li><strong>Nombre:</strong> {student.nombre}</li>
                  <li><strong>DNI:</strong> {student.dni}</li>
                  <li><strong>Nacionalidad:</strong> {student.nacionalidad}</li>
                  <li><strong>Fecha de nacimiento:</strong> {student.fechanacimiento}</li>
                </ul>
              </div>
            </Link>
          ))}
        </div>
      )}
      <GetStudent studentId={selectedStudentId} /> {/* Aquí pasas el idalumno seleccionado al componente GetStudent */}
    </div>
  );
}

export default SearchStudentComponent;
