import React from 'react';
import { useLocation } from 'react-router-dom'; // -> Esto es un hook, nos permite acceder al objeto actual.

function GetStudent() {
  const location = useLocation(); // Obtiene el objeto location
  const { studentId2 } = location.state || {}; // Obtén el idalumno del estado de navegación

/*
Si studentId recibe datos, es actualizado, navegamos hasta /getStudent
*/
useEffect(() => {
    GetStudentDataByName (studentId2);
    }, [studentId2]);


/*
Solicitamos la peticion para que nos devuela los datos del / los alumnos
que coincidan en su nombre con lo solicitado por el usuario.
*/
const GetStudentDataByName = async (studentId2) => {
    console.log("CONTENIDO: " + studentId2);
    try {
        const bodyParameters = {
        'nombre': studentId2
        };
        const options = {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(bodyParameters)
        };
        const response = await fetch("/getStudentDataByName", options);
        if (!response.ok) {
        throw new Error('Error en la solicitud');
        }
        const jsonResponse = await response.json();
    } catch (error) {
        console.error('Error:', error.message);
    }
    };

  return (
    <div>
      <p>ID del Alumno: {studentId2}</p>
    </div>
  );
}

export default GetStudent;
