import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import '../styles.css';

function Body() {
  // Definimos un estado para controlar si el submenú está abierto o cerrado
  const [submenuOpen, setSubmenuOpen] = useState(false);

  // Función para cambiar el estado del submenú
  const toggleSubmenu = () => {
    setSubmenuOpen(!submenuOpen);
  };

  return (
    <div className="body">
      <div>
      {/* Botón principal "Alumnos" */}
      <div className="button" onClick={toggleSubmenu}>
        Alumnos
        {/* Condicional para mostrar el submenú si está abierto */}
        {submenuOpen && (
          <div className="submenu">
            {/* Enlaces del submenú */}
            <div><Link to="/addStudent" className="subOption">Añadir nuevo alumno</Link></div>
            <div><Link to="/updateStudent" className="subOption">Administrar Alumnos</Link></div>
            <div><Link to="/getAllStudents" className="subOption">Mostrar Alumnos</Link></div>
          </div>
        )}
      </div>
      <div className="button" onClick={toggleSubmenu}>
        Empresas
        {/* Condicional para mostrar el submenú si está abierto */}
        {submenuOpen && (
          <div className="submenu">
            {/* Enlaces del submenú */}
            <div><Link to="/" className="subOption">Añadir nueva empresa</Link></div>
            <div><Link to="/" className="subOption">Administrar Alumnos</Link></div>
            <div><Link to="/" className="subOption">Mostrar Empresas</Link></div>
          </div>
        )}
      </div>
      <div className="button" onClick={toggleSubmenu}>
        Gestión de candidatos
        {/* Condicional para mostrar el submenú si está abierto */}
        {submenuOpen && (
          <div className="submenu">
            {/* Enlaces del submenú */}
            <div><Link to="/" className="subOption">Añadir nuevo candidato</Link></div>
            <div><Link to="/" className="subOption">Administrar Candidatos</Link></div>
            <div><Link to="/" className="subOption">Mostrar candidatos</Link></div>
          </div>
        )}
      </div>
      <Link to="/idioms" className="button">Idiomas</Link>
      <Link to="" className="button">Contactos</Link>
      </div>
      <div className="fill">
      </div>
    </div>
  );
}

export default Body;
