import React, { useState } from 'react';
import Main from './Main'; // Importa el componente Main
import '../styles.css';

function Body() {
  const [showMain, setShowMain] = useState(true); // Estado para controlar la visibilidad de Main

  const hideMain = () => {
    setShowMain(false);
  };

  return (
    <div className="body">
      {showMain && <Main />} {/* Muestra Main si showMain es true */}
      <button onClick={hideMain}>Ocultar Main</button> {/* Bot√≥n para ocultar Main */}
    </div>
  );
}

export default Body;
