import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import '../styles.css';

function GetStudentEdit() {
  const location = useLocation();
  const { studentId, studentData } = location.state || {};

  //ATRIBUTOS DE ALUMNO
  const [nombre, setNombre] = useState('');
  const [sexo, setSexo] = useState('');
  const [dni, setDni] = useState('');
  const [nacionalidad, setNacionalidad] = useState('');
  const [fechanacimiento, setFechanacimiento] = useState('');
  const [preferencia1, setPreferencia1] = useState('');
  const [preferencia2, setPreferencia2] = useState('');
  const [preferencia3, setPreferencia3] = useState('');
  const [fecha, setFecha] = useState('');
  const [anyocursado, setAnyocursado] = useState('');
  const [estadocurriculum, setEstadocurriculum] = useState('');
  const [estadoadmision, setEstadoadmision] = useState('');
  const [emailinstituto, setEmailinstituto] = useState('');
  const [email, setEmail] = useState('');
  const [telalumno, setTelalumno] = useState('');
  const [telfamilia, setTelfamilia] = useState('');
  const [carnetconducir, setCarnetconducir] = useState('');
  const [disponibilidad, setDisponibilidad] = useState('');
  const [numeroSS, setNumeroSS] = useState('');
  const [situacionlaboral, setSituacionlaboral] = useState('');
  const [mesFCT, setMesFCT] = useState('');
  const [domicilio, setDomicilio] = useState('');
  const [cp, setCp] = useState('');
  const [localidad, setLocalidad] = useState('');
  const [especialidad, setEspecialidad] = useState('');
  const [nombreTutorLegal, setNombreTutorLegal] = useState('');
  const [observaciones, setObservaciones] = useState('');
  //ATRIBUTOS DE IDIOM
  const [dataIdioms, setDataIdioms] = useState([]);
  const [idiomaalumno, setIdiomaAlumno] = useState("");
  let [idiomId, setIdiomId] = useState("");
  const [degree, setDegree] = useState("");
  //ATRIBUTOS DE DOCS
  const [studentDocument, setStudentDocument] = useState("");
  const [URL, setURL] = useState("");
  //ATRIBUTOS DE  CALIFICATION
  const [averageGrade, setAverageGrade] = useState("");
  const [idiomGrade, setIdiomGrade] = useState("");
  const [maturityGrade, setMaturityGrade] = useState("");
  const [competentGrade, setCompetentGrade] = useState("");
  const [failuresNumber, setFailuresNumber] = useState("");
  const [failuresGrade, setFailuresGrade] = useState("");
  const [globalGrade, setGlobalGrade] = useState("");
  const [observations2, setObservations2] = useState("");

  /* 
    Almacenamos automaticamente el contenido de cada atributo en cada atributo,
    solo si nuestro array studentData contiene algo.
  */
  useEffect(() => {
    if (studentData.length > 0) {
      const item = studentData[0]; // Assuming only one student data object
      setNombre(item.nombre || '');
      setSexo(item.sexo || '');
      setDni(item.dni || '');
      setNacionalidad(item.nacionalidad || '');
      setFechanacimiento(item.fechanacimiento || '');
      setPreferencia1(item.preferencia1 || '');
      setPreferencia2(item.preferencia2 || '');
      setPreferencia3(item.preferencia3 || '');
      setFecha(item.fecha || '');
      setAnyocursado(item.anyocursado || '');
      setEstadocurriculum(item.estadocurriculum || '');
      setEstadoadmision(item.estadoadmision || '');
      setEmailinstituto(item.emailinstituto || '');
      setEmail(item.email || '');
      setTelalumno(item.telalumno || '');
      setTelfamilia(item.telfamilia || '');
      setCarnetconducir(item.carnetconducir || '');
      setDisponibilidad(item.disponibilidad || '');
      setNumeroSS(item.numeroSS || '');
      setSituacionlaboral(item.situacionlaboral || '');
      setMesFCT(item.mesFCT || '');
      setDomicilio(item.domicilio || '');
      setCp(item.cp || '');
      setLocalidad(item.localidad || '');
      setEspecialidad(item.especialidad || '');
      setNombreTutorLegal(item.nombretutorlegal || '');
      setObservaciones(item.observaciones || '');
    }
  }, [studentData]);


  console.log('Student ID:', studentId);
  console.log('Student Data:', studentData);

  // Aquí puedes renderizar la información del estudiante o el formulario de edición

  return (
    <div>
      <h1>Edit Student</h1>
      <div className="results-container">
        {studentData.map((item, index) => (
          <div key={index} className="student-card">
            <ul className="student-info-list">
            <li>
              <strong>Nombre:</strong>
              <input type="text" value={nombre} onChange={(e) => setNombre(e.target.value)} />
            </li>
            <li>
              <strong>Sexo:</strong>
              <input type="text" value={sexo} onChange={(e) => setSexo(e.target.value)} />
            </li>
            <li>
              <strong>DNI:</strong>
              <input type="text" value={dni} onChange={(e) => setDni(e.target.value)} />
            </li>
            <li>
              <strong>Nacionalidad:</strong>
              <input type="text" value={nacionalidad} onChange={(e) => setNacionalidad(e.target.value)} />
            </li>
            <li>
              <strong>Fecha de nacimiento:</strong>
              <input type="date" value={fechanacimiento} onChange={(e) => setFechanacimiento(e.target.value)} />
            </li>
            <li>
              <strong>Primera preferencia:</strong>
              <input type="text" value={preferencia1} onChange={(e) => setPreferencia1(e.target.value)} />
            </li>
            <li>
              <strong>Segunda preferencia:</strong>
              <input type="text" value={preferencia2} onChange={(e) => setPreferencia2(e.target.value)} />
            </li>
            <li>
              <strong>Tercera preferencia:</strong>
              <input type="text" value={preferencia3} onChange={(e) => setPreferencia3(e.target.value)} />
            </li>
            <li>
              <strong>Fecha registro de alumno:</strong>
              <input type="date" value={fecha} onChange={(e) => setFecha(e.target.value)} />
            </li>
            <li>
              <strong>Año Cursado:</strong>
              <input type="text" value={anyocursado} onChange={(e) => setAnyocursado(e.target.value)} />
            </li>
            <li>
              <strong>Estado Curriculum:</strong>
              <input type="text" value={estadocurriculum} onChange={(e) => setEstadocurriculum(e.target.value)} />
            </li>
            <li>
              <strong>Estado Admisión:</strong>
              <input type="text" value={estadoadmision} onChange={(e) => setEstadoadmision(e.target.value)} />
            </li>
            <li>
              <strong>Email Instituto:</strong>
              <input type="email" value={emailinstituto} onChange={(e) => setEmailinstituto(e.target.value)} />
            </li>
            <li>
              <strong>Email personal:</strong>
              <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} />
            </li>
            <li>
              <strong>Teléfono Alumno:</strong>
              <input type="tel" value={telalumno} onChange={(e) => setTelalumno(e.target.value)} />
            </li>
            <li>
              <strong>Teléfono Familia:</strong>
              <input type="tel" value={telfamilia} onChange={(e) => setTelfamilia(e.target.value)} />
            </li>
            <li>
              <strong>Carnet de Conducir:</strong>
              <input type="text" value={carnetconducir} onChange={(e) => setCarnetconducir(e.target.value)} />
            </li>
            <li>
              <strong>Disponibilidad:</strong>
              <input type="text" value={disponibilidad} onChange={(e) => setDisponibilidad(e.target.value)} />
            </li>
            <li>
              <strong>Número SS:</strong>
              <input type="text" value={numeroSS} onChange={(e) => setNumeroSS(e.target.value)} />
            </li>
            <li>
              <strong>Situación Laboral:</strong>
              <input type="text" value={situacionlaboral} onChange={(e) => setSituacionlaboral(e.target.value)} />
            </li>
            <li>
              <strong>Mes FCT:</strong>
              <input type="text" value={mesFCT} onChange={(e) => setMesFCT(e.target.value)} />
            </li>
            <li>
              <strong>Domicilio:</strong>
              <input type="text" value={domicilio} onChange={(e) => setDomicilio(e.target.value)} />
            </li>
            <li>
              <strong>Código Postal:</strong>
              <input type="text" value={cp} onChange={(e) => setCp(e.target.value)} />
            </li>
            <li>
              <strong>Localidad:</strong>
              <input type="text" value={localidad} onChange={(e) => setLocalidad(e.target.value)} />
            </li>
            <li>
              <strong>Especialidad:</strong>
              <input type="text" value={especialidad} onChange={(e) => setEspecialidad(e.target.value)} />
            </li>
            <li>
              <strong>Nombre Tutor Legal:</strong>
              <input type="text" value={nombreTutorLegal} onChange={(e) => setNombreTutorLegal(e.target.value)} />
            </li>
            <li>
              <strong>Observaciones:</strong>
              <input type="text" value={observaciones} onChange={(e) => setObservaciones(e.target.value)} />
            </li>
            </ul>
          </div>
        ))}
      </div>
    </div>
  );
}

export default GetStudentEdit;
